targets.query:
from(bucket: "nifi/autogen")
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "flow/status" and
    r._field == "controllerStatus.runningCount" or r._field == "controllerStatus.disabledCount" or r._field == "controllerStatus.stoppedCount" or r._field == "controllerStatus.syncFailureCount"
  )

import "strings"
from(bucket: "nifi/autogen")
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "system-diagnostics" )
  |> filter(fn: (r) => r._field == "systemDiagnostics.aggregateSnapshot.heapUtilization")
  |> map(fn: (r) => ({
      r with
      _value: strings.substring(v: r._value, start: 0, end: 4)
    })
  )
  |> toFloat()
  |> aggregateWindow(every: 5m, fn: mean)
